import React, { Component } from 'react';
import SubjectList from './SubjectList.js'
import TodoList from './TodoList.js'
import Search from './Search.js'
import Insert from './Insert.js'
import {data} from './data/data.js'

class App extends Component {
  constructor(props){
    super(props);
    this.state ={
      data : data,
      currentData : data[0],
      id : 1
    }
  }

  insertSubjectHandler = () => {
    let id = this.state.id;
    let newData = {
      id : id,
      subject : 'new subject',
      todolist : []
    };
    data.push(newData)
    this.setState({
      currentData : data[data.length - 1],
      id : id + 1
    })
  }

  mouseDownHandler = (e) => {
    console.log(e.which)
  }
  render() {
    return (
      <div className="App">
        <div className="todolist-left">
          <Search />
          <SubjectList subjects={this.state.data}/>
          <Insert insert={this.insertSubjectHandler}/>
        </div>
        <div className="todolist-right">
          <TodoList todos={this.state.currentData}/>
        </div>
      </div>
    );
  }
}

export default App;
