import React, { Component } from 'react';
import SubjectList from './SubjectList.js'
import TodoList from './TodoList.js'
import Search from './Search.js'
import InsertSubject from './InsertSubject.js'
import {data} from './data/data.js'

class App extends Component {
  constructor(props){
    super(props);
    this.state ={
      data : data,
      currentData : data[0],
      subjectId : 1
    }
  }

  // subject insert button handler
  insertSubjectHandler = () => {
    let id = this.state.subjectId;
    let newData = {
      subjectId : id,
      subject : 'new subject' + id,
      todolist : []
    };
    data.push(newData)
    this.setState({
      currentData : data[data.length - 1],
      subjectId : id + 1,
      inputData : {}
    })
  }

  // subject 우클릭 event handler
  contextMenuHandler = (e) => {
    console.log(e)
  }

  // subject 좌클릭 시 todolist 변경 event handler
  onClickSubjectHandler = (data) => {
    this.setState({
      currentData : data
    })    
  }

  // todolist insert button click event handler
  todoFocusHandler = () => {
    let todoEntries = document.getElementsByClassName("todo-entry");
    if(todoEntries[todoEntries.length - 1].value === "") {
      console.log('null')
    }
  }

  // todolist onchange event handler
  insertTodoHandler = (e) => {
    let currentData = this.state.currentData;
    currentData.todolist.push(e.target.value);
    let data = this.state.data;
    data.forEach((object)=> {
      if(object.subject === currentData.subject) {
        console.log('hi')
        object.todolist.push(e.target.value);
      }
    })
    this.setState({
      currentData : currentData,
      data : data
    })
    console.log(this.state.currentData, this.state.data)
  }

  render() {
    return (
      <div className="App">
        <div className="todolist-left">
          <Search />
          <SubjectList subjects={this.state.data} onClickSubjectHandler={this.onClickSubjectHandler}
            contextMenuHandler={this.contextMenuHandler}/>
          <InsertSubject insert={this.insertSubjectHandler}/>
        </div>
        <div className="todolist-right">
          <TodoList todos={this.state.currentData} todoFocusHandler={this.todoFocusHandler}
            insertTodoHandler={this.insertTodoHandler}/>
        </div>
      </div>
    );
  }
}

export default App;
