import React, { Component } from 'react';
import SubjectList from './SubjectList.js'
import TodoList from './TodoList.js'
import Search from './Search.js'
import InsertSubject from './InsertSubject.js'
import {data} from './data/data.js'

class App extends Component {
  constructor(props){
    super(props);
    this.state ={
      data : data,
      currentData : data[0],
      subjectId : 1,
      todoIndex : data[0].todolist.length
    }
  }

  // subject insert button handler
  insertSubjectHandler = () => {
    let id = this.state.subjectId;
    let newData = {
      subjectId : id,
      subject : 'new subject' + id,
      todolist : []
    };
    data.push(newData)
    this.setState({
      currentData : data[data.length - 1],
      subjectId : id + 1,
      inputData : {}
    })
  }

  // subject 우클릭 event handler
  contextMenuHandler = (e) => {
    console.log(e)
  }

  // subject 좌클릭 시 todolist 변경 event handler
  onClickSubjectHandler = (data) => {
    this.setState({
      currentData : data
    })    
  }

  // todolist insert button click event handler
  todoFocusHandler = () => {
    console.log(1)
  }

  // todolist onchange event handler
  insertTodoHandler = (e, subject) => {
    let cloneData = this.state.data; 
    let value = e.target.value;
    let todoIndex = this.state.todoIndex;
    if(value) {
      cloneData.map((object) => {
        if(object.subject === subject) {
          if(object.todolist[todoIndex]) {
            object.todolist[todoIndex] = value;
          } else {
            object.todolist.push(value);
          }
        }
      })
    }       
    this.setState({
      data : cloneData
    })

  }

  onFocusOutHandler = () => {

  }

  render() {
    return (
      <div className="App">
        <div className="todolist-left">
          <Search />
          <SubjectList subjects={this.state.data} onClickSubjectHandler={this.onClickSubjectHandler}
            contextMenuHandler={this.contextMenuHandler}/>
          <InsertSubject insert={this.insertSubjectHandler}/>
        </div>
        <div className="todolist-right">
          <TodoList todos={this.state.currentData} todoFocusHandler={this.todoFocusHandler}
            insertTodoHandler={this.insertTodoHandler} onFocusOutHandler={this.onFocusOutHandler}/>
        </div>
      </div>
    );
  }
}

export default App;
